mapper.select:



		AND menu_key=#{menu.menuKey}


		AND menu_name like concat('%', #{menu.menuName}, '%')


		AND visible = #{menu.visible}


order by order_num

mapper.update:
update sys_menu
menu_name = #{menuName}, parent_id = #{parentId}, order_num = #{orderNum}, menu_type = #{menuType}, target = #{target}, menu_key = #{menuKey}, component = #{component}, visible = #{visible}, perms = #{perms}, icon = #{icon}, remark = #{remark}, update_by = #{updateBy}, update_time = sysdate()
where menu_id = #{menuId}
